global guess
guess = random.randint(1,101)

def checker(my_guess):
    if my_guess>guess:
        print("Too high.")
    elif my_guess<guess:
        print("Too low.")
    else:
        print(f"You got it! The answer was {guess}")
        print("Guess again.")
        

def game(level):
    ref = {'easy':10, 'hard':5}

    attempts = ref[level.lower()]
    while attempts!=0:
        print(f"You have {attempts} attempts remaining to guess the number")
        my_guess = int(input("Make a guess: "))
        checker(my_guess)
        attempts-=1
    print("You've run out of guesses, you lose.")

level = input("""Welcome to th Number Guessing Game!
I'm thinking of a number between 1 and 100.
Choose a difficulty. Type 'easy' or 'hard: """)

game(level)
 
 